var urlLat=location.search.match(new RegExp("lat=(.*?)($|&)","i"))[1];var urlLong=location.search.match(new RegExp("long=(.*?)($|&)","i"))[1];var urlType=location.search.match(new RegExp("type=(.*?)($|&)","i"))[1];var urlZoom=1*location.search.match(new RegExp("zoom=(.*?)($|&)","i"))[1];var urlMarkerTip=decodeURIComponent(location.search.match(new RegExp("markertip=(.*?)($|&)","i"))[1]);switch(urlType){case"hybrid":var thisType=google.maps.MapTypeId.HYBRID;break;case"roadmap":var thisType=google.maps.MapTypeId.ROADMAP;break;case"satellite":var thisType=google.maps.MapTypeId.SATELLITE;break;case"terrain":var thisType=google.maps.MapTypeId.TERRAIN;break}function initialize(){var b=new google.maps.LatLng(urlLat,urlLong);var c={zoom:urlZoom,center:b,mapTypeId:thisType};var d=new google.maps.Map(document.getElementById("map-canvas"),c);var a=new google.maps.Marker({position:b,map:d,title:urlMarkerTip});google.maps.event.addDomListener(window,"resize",function(){var e=d.getCenter();google.maps.event.trigger(d,"resize");d.setCenter(e)})}google.maps.event.addDomListener(window,"load",initialize);